export const getCategoryById = (catId: string) => inspectionMaster.find(c => c.id === catId);
export const getItemById = (itemId: string) => {
  for (const cat of inspectionMaster) {
    const item = cat.items.find(i => i.id === itemId);
    if (item) return { item, category: cat };
  }
  return null;
};
export const getTotalItemCount = () => inspectionMaster.reduce((sum, cat) => sum + cat.items.length, 0);
